'use strict'

const tap = require('tap')
const path = require('path')

const { readAndStripPrivateKeyContent } = require('../../lib/gcpKeyStripper')

tap.test('throw exception for key not found', (t) => {
  t.throws(() => readAndStripPrivateKeyContent('invalid_key.pem'))
  t.end()
})

tap.test('correctly strip one line key', (t) => {
  const strippedContent = readAndStripPrivateKeyContent(path.join(__dirname, './oneLineKey.pem'))
  t.equal(strippedContent, '')
  t.end()
})
